    private void ChooseFile(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_ChooseFile
        int status = jFileChooser1.showOpenDialog(this);
        if (status == JFileChooser.APPROVE_OPTION){
            file = jFileChooser1.getSelectedFile();
            try {
                image = ImageIO.read(file);
            } catch (IOException ex) {
                JOptionPane.showMessageDialog(null, "Unsupported image type", "Error", JOptionPane.ERROR_MESSAGE);
                Logger.getLogger(CreateAuction.class.getName()).log(Level.SEVERE, null, ex);
            }
            scaledImage = Utilities.scaleImage(image, 150, 150);
            icon = new ImageIcon(scaledImage);
            jLabel7.setIcon(icon);
            jLabel7.setVisible(true);
            jPanel1.setVisible(true);
        }
    }//GEN-LAST:event_ChooseFile

    private void AddItem(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_AddItem
        PreparedStatement preparedStatement = null;
        String title = jTextField1.getText();
        int price = Integer.parseInt(jTextField2.getText());
        int time = Integer.parseInt(jTextField3.getText());
        String category = (String) jComboBox1.getSelectedItem();        
        String description = jTextArea1.getText();
        Date date = new java.util.Date();
        java.sql.Date sqlDate = new java.sql.Date(date.getTime());
        
        //If time is 0 or we have no title we throw error to the user
        if (title.equals("") || price == 0 || time == 0 || image == null){
            JOptionPane.showMessageDialog(null, "All fields required.", "Error", JOptionPane.INFORMATION_MESSAGE);
        }else{
            String sql = "INSERT INTO ITEMS (Name,Category,Description,Created_on,Remaining_Time,Price,Seller,Img) values (?,?,?,?,?,?,?,?)";

            //Get database connection
            Connection conn = SQLConn.sql_open();

            try {
                preparedStatement =  conn.prepareStatement(sql);
                preparedStatement.setString(1, title);
                preparedStatement.setString(2, category);
                preparedStatement.setString(3, description);
                preparedStatement.setDate(4, sqlDate);
                preparedStatement.setInt(5, time);
                preparedStatement.setInt(6, price);
                preparedStatement.setString(7, user);
                try {
                    preparedStatement.setBytes(8, Utilities.img2Byte(image));
                } catch (IOException ex) {
                    Logger.getLogger(CreateAuction.class.getName()).log(Level.SEVERE, null, ex);
                }
                preparedStatement.execute();
                SQLConn.sql_close(conn);
                JOptionPane.showMessageDialog(null, "Item has now added in auction list!", "Items", JOptionPane.INFORMATION_MESSAGE);
                dispose();

            } catch (SQLException ex) {
                Logger.getLogger(JFrame.class.getName()).log(Level.SEVERE, null, ex);
            }
        }
    }//GEN-LAST:event_AddItem
     
